select a1.machine_id,CAST(ROUND(AVG(a2.timestamp - a1.timestamp), 3) AS FLOAT) as processing_time from Activity as a1 inner join Activity as a2 on a1.machine_id=a2.machine_id and a1.process_id = a2.process_id and a1.activity_type!=a2.activity_type where a1.timestamp < a2.timestamp group by a1.machine_id